async function ensureOffscreenDocument(){await chrome.offscreen.hasDocument()||await chrome.offscreen.createDocument({url:"offscreen.html",reasons:["BLOBS"],justification:"Process image using Tesseract.js in an offscreen document"})}chrome.runtime.onMessage.addListener((async(e,n,s)=>("startOffscreen"===e.action&&e.imageUrl&&(await ensureOffscreenDocument(),chrome.runtime.sendMessage(e)),!0)));